 = Provider.Database.Read(typeof(Project), id);\r\n$\r\n\r\n<div class=\"page-header\">\r\n    <h1 class=\"orange\">\r\n        <i class=\"icon-briefcase\"></i>\r\n        Project\r\n        <small>\r\n			<i class=\"icon-double-angle-right\"></i>\r\n			View project details\r\n		</small>\r\n	</h1>\r\n</div>\r\n\r\n<div id=\"user-profile-1\" class=\"user-profile row\">\r\n	<div class=\"col-xs-12 col-sm-3 center\">\r\n		<div>\r\n			<span class=\"profile-picture\">\r\n				<img id=\"avatar\" class=\"img-responsive\" alt=\"Avatar\" src=\"$=(e.Picture : \'/UserFiles/project.png\')$\" />\r\n			</span>\r\n\r\n			<div class=\"space-4\"></div>\r\n\r\n            <div>\r\n				<div class=\"inline position-relative\">\r\n					<a href=\"#\" class=\"user-title-label dropdown-toggle\" data-toggle=\"dropdown\">\r\n						<i class=\"icon-circle light-$=e.DueDate>DateTime.Now ? \'green\':\'red\'$\"></i>\r\n						&nbsp;\r\n						<span>$=e.Name$</span>\r\n					</a>\r\n\r\n    				<ul class=\"align-left dropdown-menu dropdown-caret dropdown-lighter\">\r\n						<li class=\"dropdown-header\"> Process </li>\r\n\r\n						<li>\r\n							<a href=\"/IssueProjectEdit.aspx?eId=$=e.Id$\">\r\n								<i class=\"icon-pencil green\"></i>&nbsp;\r\n								<span class=\"green\">Edit</span>\r\n							</a>\r\n						</li>\r\n\r\n						<li>\r\n							<a href=\"#\">\r\n								<i class=\"icon-trash red\"></i>&nbsp;\r\n								<span class=\"red\">Archive</span>\r\n							</a>\r\n						</li>\r\n\r\n					</ul>\r\n				</div>\r\n			</div>\r\n            \r\n		</div>\r\n\r\n\r\n		<div class=\"hr hr12 dotted\"></div>\r\n\r\n		<div class=\"clearfix\">\r\n			<div class=\"grid2\">\r\n				<span class=\"bigger-175 blue\">$=e.GetTasks().Count $</span><br />\r\n				Tasks\r\n			</div>\r\n\r\n			<div class=\"grid2\">\r\n				<span class=\"bigger-175 blue\">$=Context.GetValue(\"select count(distinct AssignedToId) from Task where ProjectId={0}\", e.Id) $</span><br />\r\n				Team Members\r\n			</div>\r\n		</div>\r\n\r\n		<div class=\"hr hr16 dotted\"></div>\r\n	</div>\r\n\r\n	<div class=\"col-xs-12 col-sm-9\">\r\n\r\n		<div class=\"profile-user-info profile-user-info-striped\">\r\n\r\n    		<div class=\"profile-info-row\">\r\n				<div class=\"profile-info-name\"> Name </div>\r\n\r\n				<div class=\"profile-info-value\">\r\n					<b>$=e.Name$</b>\r\n				</div>\r\n			</div>\r\n\r\n        	<div class=\"profile-info-row\">\r\n				<div class=\"profile-info-name\"> Description </div>\r\n\r\n				<div class=\"profile-info-value\">\r\n					$=e.Description$\r\n				</div>\r\n			</div>\r\n\r\n        	<div class=\"profile-info-row\">\r\n				<div class=\"profile-info-name\"> Prj. Manager </div>\r\n\r\n				<div class=\"profile-info-value\">\r\n					$=e.Manager.FullName$\r\n				</div>\r\n			</div>\r\n\r\n            <div class=\"profile-info-row\">\r\n    			<div class=\"profile-info-name\"> Start Date </div>\r\n\r\n				<div class=\"profile-info-value\">\r\n					$=e.StartDate.toString(\"dd-MM-yyyy\")$\r\n				</div>\r\n			</div>\r\n\r\n            <div class=\"profile-info-row\">\r\n    			<div class=\"profile-info-name\"> Due Date </div>\r\n\r\n				<div class=\"profile-info-value\">\r\n					$=e.DueDate.toString(\"dd-MM-yyyy\")$\r\n				</div>\r\n			</div>\r\n\r\n			<div class=\"profile-info-row\">\r\n				<div class=\"profile-info-name\"> Added </div>\r\n\r\n				<div class=\"profile-info-value\">\r\n					by $=e.InsertUser.FullName$ on $=e.InsertDate.toString(\"dd-MM-yyyy\")$\r\n				</div>\r\n			</div>\r\n		</div>\r\n\r\n		<div class=\"space-20\"></div>\r\n\r\n\r\n\r\n        <div class=\"widget-box transparent\" id=\"recent-box\">\r\n            <div class=\"widget-header\">\r\n    \r\n        		<div class=\"widget-toolbar no-border\">\r\n        			<ul class=\"nav nav-tabs\" id=\"recent-tab\">\r\n        				<li class=\"active\">\r\n        					<a data-toggle=\"tab\" href=\"#task-tab\"><i class=\"icon-check red\"></i> Tasks</a>\r\n        				</li>\r\n        \r\n        				<li>\r\n        					<a data-toggle=\"tab\" href=\"#member-tab\"><i class=\"icon-comment green\"></i> Team Members</a>\r\n        				</li>\r\n        \r\n        				<li>\r\n        					<a data-toggle=\"tab\" href=\"#comment-tab\"><i class=\"icon-comment blue\"></i> Comments</a>\r\n        				</li>\r\n        			</ul>\r\n        		</div>\r\n        	</div>\r\n        \r\n        	<div class=\"widget-body\">\r\n        		<div class=\"widget-main padding-4\">\r\n        			<div class=\"tab-content padding-8 overflow-visible\">\r\n        				<div id=\"task-tab\" class=\"tab-pane active\">\r\n                			<div class=\"dialogs\" style=\"max-height:300px;overflow-y:scroll\">\r\n        					<ul id=\"tasks\" class=\"item-list\" style=\"width:auto\">\r\n                                $\r\n                                function getColor(per, status){\r\n                                    if(status==\'Completed\')\r\n                                        return \'green\';\r\n                                    if(status==\'Rejected\')\r\n                                        return \'red\';\r\n                                    if(status==\'Accepted\')\r\n                                        return \'orange\';\r\n                                    return \'blue\';\r\n                                }\r\n                                foreach(var t in Provider.Database.ReadList(typeof(Task), \"select\r\n                                                Id,\r\n                                                AssignedToId,\r\n                                                Name,\r\n                                                Status,\r\n                                                case when Status=\'Accepted\' then 1\r\n                                                     when Status=\'New\' then 2\r\n                                                     when Status=\'Rejected\' then 3\r\n                                                     else 4 end as OrderNo,\r\n                                                round((now()-StartDate)/(DueDate-StartDate)*100) as Percent\r\n                                            from\r\n                                            	Task\r\n                                            where ProjectId=\"+e.Id+\" order by 5, Id desc\")){\r\n                                $\r\n        						<li class=\"item-$=getColor(t.Percent, t.Status)$ clearfix\">\r\n        							<label class=\"inline\">\r\n        								<span class=\"lbl\"><img src=\"$=t.AssignedTo.Avatar ?? \'/UserFiles/contact.png\'$\" height=\"32\"/> &nbsp; <a href=\"/IssueTAskView.aspx?eId=$=t.Id$\">$=Utility.StrCrop(t.Name, 70)$</a></span>\r\n        							</label>\r\n                                    $ if(t.Status==\'Accepted\' || t.Status==\'New\'){ $\r\n        							<div class=\"pull-right easy-pie-chart percentage\" data-size=\"30\" data-color=\"#ECCB71\" data-percent=\"$=t.Percent>100 ? 100 : (t.Percent<0 ? 0 : t.Percent)$\">\r\n        								<span class=\"percent\">$=t.Percent>100 ? \'!\' : ((t.Percent<0 ? 0 : t.Percent)+\'%\')$</span>\r\n        							</div>\r\n                                    $ } $\r\n        						</li>\r\n                                $\r\n                                }\r\n                                $\r\n        					</ul>\r\n            				</div>\r\n        \r\n\r\n        						<div class=\"form-actions\">\r\n        							<div class=\"input-group\">\r\n        								<input placeholder=\"Type new task here...\" type=\"text\" class=\"col-sm-5\" id=\"message\" name=\"message\" />\r\n                                        <select id=\"AssignedToId\" name=\"AssignedToId\" class=\"col-sm-4\">\r\n                                            <option value=\"0\">Assigned To</option>\r\n                                            $\r\n                                            foreach(var dr in Provider.Database.GetDataTable(\"select Id, Name from User order by Name\").Rows)\r\n                                                echo(\'<option value=\"\'+dr.Id+\'\"\'+(e.AssignedToId==dr.Id ? \' selected\':\'\')+\'>\'+dr.Name+\'</option>\\r\\n\');\r\n                                            $\r\n                                        </select>\r\n                                        <input class=\"date-picker col-sm-3\" type=\"text\" name=\"DueDate\" id=\"DueDate\" data-date-format=\"dd-mm-yyyy\" placeholder=\"Due Date\"/>\r\n                                        <span class=\"input-group-btn\">\r\n        									<button class=\"btn btn-sm btn-info no-radius\" type=\"button\" onclick=\"saveEntity(\'Task\', {Name:\\$(\'#message\').val(), ProjectId:$=e.Id$, AssignedToId:\\$(\'#AssignedToId\').val(), DueDate:\\$(\'#DueDate\').val()}, function(){location.reload();});\">\r\n        										<i class=\"icon-share\"></i>\r\n        										Send\r\n        									</button>\r\n        								</span>\r\n        							</div>\r\n        						</div>\r\n\r\n        				</div>\r\n        \r\n        				<div id=\"member-tab\" class=\"tab-pane\">\r\n                			<div class=\"dialogs\" style=\"max-height:300px;overflow-y:scroll\">\r\n                            $\r\n                            foreach(var ec in Provider.Database.ReadList(typeof(User), \"select\r\n                                            u.Id,\r\n                                        	u.Name,\r\n                                            u.Avatar,\r\n                                            min(t.InsertDate) as InsertDate\r\n                                        from\r\n                                        	Task t\r\n                                        	left join User u ON u.Id = t.AssignedToId\r\n                                        where\r\n                                            t.ProjectId = \"+e.Id+\"\r\n                                        group by 1,2,3\r\n                                        order by 4\")){\r\n                            $\r\n        						<div class=\"itemdiv memberdiv\">\r\n        							<div class=\"user\">\r\n        								<img src=\"$=ec.Avatar$\" width=\"48\" />\r\n        							</div>\r\n        \r\n        							<div class=\"body\">\r\n        								<div class=\"name\">\r\n        									<a href=\"/IssueTask.aspx?UserId=$=ec.Id$&ProjectId=$=e.Id$\">$=ec.Name$</a>\r\n        								</div>\r\n        \r\n        								<div class=\"time\">\r\n        									<i class=\"icon-time\"></i>\r\n        									<span class=\"green\">$=ec.InsertDate.ToString(\"dd-MM-yyyy\")$</span>\r\n        								</div>\r\n        \r\n        							</div>\r\n        						</div>\r\n                            $\r\n                            }\r\n                            $\r\n        					</div>\r\n            				<form>\r\n        						<div class=\"form-actions\">\r\n        							<div class=\"input-group\">\r\n        								<input style=\"visibility:hidden\" type=\"text\" class=\"form-control\" />\r\n        							</div>\r\n        						</div>\r\n        					</form>\r\n                        </div>\r\n        \r\n        				<div id=\"comment-tab\" class=\"tab-pane\">\r\n            				<div class=\"dialogs\" style=\"max-height:300px;overflow-y:scroll\">\r\n                            $\r\n                            foreach(var cc in Provider.Database.ReadList(typeof(EntityComment), \"select * from EntityComment where EntityName=\'Project\' AND EntityId=\"+e.Id+\" order by Id desc\")){\r\n                            $\r\n        						<div class=\"itemdiv dialogdiv\">\r\n        							<div class=\"user\">\r\n        								<img src=\"$=cc.InsertUser.Avatar$\" />\r\n        							</div>\r\n        \r\n        							<div class=\"body\">\r\n        								<div class=\"time\">\r\n        									<i class=\"icon-clock-o\"></i>\r\n        									<span class=\"green\">$=Utility.ToAgoString(cc.InsertDate)$</span>\r\n        								</div>\r\n        \r\n        								<div class=\"na